import React, { Component } from 'react';
import { ForceGraph2D } from 'react-force-graph';
import { Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, FormGroup, Label, Input } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css'
export default class Try extends Component {
    constructor(props) {
        super(props);
        // Don't call this.setState() here!
        this.state = { 
            dropdownOpen: false,
            idCounter: 3,
            cityIdCounter: 2,
            PPIdCounter:2,
            source: {},
            startValue: 0,
            selectedPlant: 'wind',
            selectedColor: 'black',
            tidalValue: 50,
            solarValue: 50,
            windValue: 50,
            data: {
                nodes: [ 
                    { 
                      "id": "id1",
                      "name": "name1",
                      "val": 1,
                      isCity: false, 
                      color: 'red',


                    },
                    { 
                      "id": "id2",
                      "name": "name2",
                      "val": 0.5,
                      isCity: true,
                    },
                ],
                links: [
                    {
                        "source": "id1",
                        "target": "id2"
                    },
                ]
            }
        };
        this.handleAddNode = this.handleAddNode.bind(this);
        this.toggleDropDown = this.toggleDropDown.bind(this);
        this.handleSetSource = this.handleSetSource.bind(this);
        this.handlePlantChange = this.handlePlantChange.bind(this);
        this.handleStartValueChange = this.handleStartValueChange.bind(this);
        this.handleTidalValueChange = this.handleTidalValueChange.bind(this);
        this.handleSolarValueChange = this.handleSolarValueChange.bind(this);
        this.handleWindValueChange = this.handleWindValueChange.bind(this);
    }
    handleAddNode(isCity){
        console.log(this.state.source.isCity)
        var color
        if(isCity){
            color = 'black'
        }   
        else{
            switch(this.state.selectedPlant){
            case 'wind':
                color = 'grey'
                break;
            case 'tidal':
                color = 'blue'
                break;
            case 'solar':
                color = 'yellow'
                break;
            default:
            }
        }


        if(Object.keys(this.state.source).length && this.state.source.isCity === !isCity){
            this.setState(prevState => ({
                data: {
                    nodes:[
                        ...prevState.data.nodes,
                        {
                            id: "id" + prevState.idCounter,
                            name: (isCity? 'City'+ prevState.cityIdCounter:'PowerPlant'+ prevState.PPIdCounter) ,
                            val: prevState.startValue/10,
                            isCity: isCity,
                            color: color
                        }                
                    ],
                    links: [

                        ...prevState.data.links,
                        {
                            source: "id" + prevState.idCounter,
                            target: prevState.source.id
                        }
                    ]
                },
                idCounter: prevState.idCounter + 1,
                selectedColor: color,
                cityIdCounter:  prevState.cityIdCounter + 1,//OBSOLETE
                PPIdCounter:  prevState.PPIdCounter + 1,//OBSOLETE
            }))
        }
        console.log(this.state)
    }
    handleSetSource(source){
        console.log(source)
        this.setState({
            source: source
        })
    }
    toggleDropDown() {
        this.setState(prevState => ({
          dropdownOpen: !prevState.dropdownOpen
        }));
    }
    handleStartValueChange(event){
           this.setState({startValue: event.target.value});
    }
    handleTidalValueChange(event){
        this.setState({tidalValue: event.target.value});
    }        
    handleSolarValueChange(event){
        this.setState({solarValue: event.target.value});
    }
    handleWindValueChange(event){
        this.setState({windValue: event.target.value});
    }
    handlePlantChange = changeEvent => {
        this.setState({
          selectedPlant: changeEvent.target.value
        });
      };
    handleTurn(){
        var cities = this.state.nodes.filter(e => e.isCity)
        console.log(cities)
        var powerPlants = this.state.nodes.filter(e => !e.isCity)
        console.log(powerPlants)





    }
    getRandomInt(range){
        return Math.floor(Math.random()*(range))-(range/2);
    }
    render() {
        const data = this.state.data
        const sourceName = this.state.source.name
        const startValue = this.state.startValue
        const tidalValue = this.state.tidalValue
        const solarValue = this.state.solarValue
        const windValue = this.state.windValue

      return(
          <div>
            <p>hallo</p>
            <Button onClick={()=>this.handleAddNode(true)}>add a City</Button>
            <Button onClick={()=>this.handleAddNode(false)}>add a Power Plant</Button>
            <Button onClick={()=>this.handleTurn()}>One Turn</Button>
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggleDropDown}>
                <DropdownToggle caret>
                    <p>FirstConnection</p>
                    {sourceName}
                </DropdownToggle>
                <DropdownMenu>

                {this.state.data.nodes.map((item, i )=> <DropdownItem key={i} onClick={() => this.handleSetSource(item, i)}>{item.name}</DropdownItem>)}

                </DropdownMenu>
            </Dropdown>
            <div  style={{display: 'flex', flexDirection: 'column'}}>
                <label>Starting Value: {startValue}</label>
                <input type="range" max="100" step="1" value={this.state.startValue} onChange={this.handleStartValueChange}></input>
                <label>Tidal Value: {tidalValue}</label>
                <input type="range" max="100" step="1" value={this.state.tidalValue} onChange={this.handleTidalValueChange}></input>
                <label>Solar Value: {solarValue}</label>
                <input type="range" max="100" step="1" value={this.state.solarValue} onChange={this.handleSolarValueChange}></input>
                <label>Wind Value: {windValue}</label>
                <input type="range" max="100" step="1" value={this.state.windValue} onChange={this.handleWindValueChange}></input>
            </div>

            <div>
            <FormGroup tag="fieldset">
                <FormGroup check>
                    <Label check>
                    <Input type="radio" name="radio1" 
                        value={"tidal"}
                        checked={this.state.selectedPlant === "tidal"}
                        onChange={this.handlePlantChange}
                    />{' '}
                        Tidal 
                    </Label>
                </FormGroup>
                <FormGroup check>
                    <Label check>
                    <Input type="radio" name="radio1" 
                        value={"solar"}
                        checked={this.state.selectedPlant === "solar"}
                        onChange={this.handlePlantChange}
                    />{' '}
                        Solar
                    </Label>
                </FormGroup>
                <FormGroup check>
                    <Label check>
                    <Input type="radio" name="radio1"
                        value={"wind"}
                        checked={this.state.selectedPlant === "wind"}
                        onChange={this.handlePlantChange}
                    />{' '}
                        Wind
                    </Label>
                </FormGroup>
            </FormGroup> 
            </div>
            <ForceGraph2D graphData={data}></ForceGraph2D>

          </div>

      )
    }
  }
