                isCity:true,
                color:'black'
            })
        })
        let color
        this.state.plants.map(e =>{
            switch(e.type){
                case WIND:
                    color = 'grey'
                    break
                case TIDAL:
                    color = 'blue'
                    break;
                default:
                    color = 'yellow'
            }
            e.connections.map(c=>{
                links.push({
                    source:e.id,
                    target:c
                })
            })
            nodes.push({
                name:'Plant'+e.id,
                id:e.id,
                val:e.multiplier*5,
                isCity:false,
                color:color
            })
        })
