        this.state = { 
            dropdownOpen: false,
            idCounter: 3,
            source: '',
            data: {
                nodes: [ 
                    { 
                      "id": "id1",
                      "name": "name1",
                      "val": 1 
                    },
                    { 
                      "id": "id2",
                      "name": "name2",
                      "val": 10 
                    },
                ],
                links: [
                    {
                        "source": "id1",
                        "target": "id2"
                    },
                ]
            }
        };
        this.handleAddNode = this.handleAddNode.bind(this);
        this.toggleDropDown = this.toggleDropDown.bind(this);
        this.handleSetSource = this.handleSetSource.bind(this);
    }
    handleAddNode(){
        this.setState(prevState => ({
            data: {
                nodes:[
                    ...prevState.data.nodes,
                    {
                        id: "id" + prevState.idCounter,
                        name: "name" + prevState.idCounter,
                        val: 20
                    }                
                ],
