      return(
          <div>
            <p>hallo</p>
            <Button onClick={this.handleAddNode}>add a Node</Button>
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggleDropDown}>
                <DropdownToggle caret>
                    <p>FirstConnection</p>
                    {sourceName}
                </DropdownToggle>
                <DropdownMenu>

                {this.state.data.nodes.map((item, i )=> <DropdownItem key={i} onClick={() => this.handleSetSource(item, i)}>{item.name}</DropdownItem>)}

