import React, {Component} from 'react'
import {ForceGraph2D} from 'react-force-graph'
import {Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, FormGroup, Label, Input} from 'reactstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

const WIND = 'WIND'
const TIDAL = 'TIDAL'
const SOLAR = 'SOLAR'

const SATURATED = 'SATURATED'
const OVER_SATURATED = 'OVER_SATURATED'
const UNDER_SATURATED = 'UNDER_SATURATED'

export default class Try extends Component {
    constructor(props) {
        super(props)
        // Don't call this.setState() here!
        this.state = {
            dropdownOpen: false,
            dropdownOpen2: false,
            idCounter: 5,
            source: {},
            drain: {},
            startValue: 0,
            selectedPlant: 'wind',
            wind: 50,
            tidal: 50,
            solar: 50,
            cities: {
                1: {
                    currentSaturation: 0,
                    totalDemand: 250,
                    name: 'Berlin'
                }
                ,
                2: {
                    currentSaturation: 0,
                    totalDemand: 250,
                    name: 'Tokyo'
                }
            },
            plants: {
                3: {
                    multiplier: 1,
                    type: WIND,
                    connections: [{1: 50}],
                    previousDecisions: [SATURATED, OVER_SATURATED, SATURATED, OVER_SATURATED, UNDER_SATURATED],
                },
                4: {
                    multiplier: 1,
                    type: TIDAL,
                    connections: [{1: 40}, {2: 60}],
                    previousDecisions: [SATURATED, OVER_SATURATED, SATURATED, OVER_SATURATED, SATURATED],
                }
            },
            data: {
                nodes: [
                    {
                        'id': 'id1',
                        'name': 'name1',
                        'val': 1,
                        isCity: false,
                        color: 'red',
                    },
                    {
                        'id': 'id2',
                        'name': 'name2',
                        'val': 0.5,
                        isCity: true,
                    },
                ],
                links: [
                    {
                        source: 'id1',
                        target: 'id2'
                    },
                ]
            },
        }
        this.handleAddNode = this.handleAddNode.bind(this)
        this.handleAddEdge = this.handleAddEdge.bind(this)
        this.toggleDropDown = this.toggleDropDown.bind(this)
        this.toggleDropDown2 = this.toggleDropDown2.bind(this)
        this.handleSetSource = this.handleSetSource.bind(this)
        this.handlePlantChange = this.handlePlantChange.bind(this)
        this.handleStartValueChange = this.handleStartValueChange.bind(this)
        this.handleTidalValueChange = this.handleTidalValueChange.bind(this)
        this.handleSolarValueChange = this.handleSolarValueChange.bind(this)
        this.handleWindValueChange = this.handleWindValueChange.bind(this)
        this.handleTurn = this.handleTurn.bind(this)
        this.formatData = this.formatData.bind(this)
    }
    componentWillMount(){
        this.formatData()
    }
    handleAddNode(isCity) {
        console.log(this.state.source.isCity)
        var type
        if (isCity) {
            type = 'black'
        } else {
            switch (this.state.selectedPlant) {
                case 'wind':
                    type = WIND
                    break
                case 'tidal':
                    type = TIDAL
                    break
                case 'solar':
                    type = SOLAR
                    break
                default:
            }
        }


        if (Object.keys(this.state.source).length ) {
            let cities = this.state.cities
            let id = this.state.idCounter
            let sourceID = this.state.source.id
            cities[id] = {
                currentSaturation: 0,
                name: 'City' + id,
                totalDemand: this.state.startValue * 3,
            }
            if(isCity){this.setState(prevState => ({
               cities:cities,    
               links: [
                    ...prevState.data.links,
                    {
                        source: prevState.idCounter,
                        target: prevState.drain.id
                    }
                ],
                idCounter: prevState.idCounter + 1,

            }))

            }else{
                let plants = this.state.plants
                plants[id] = {
                    multiplier: 1,
                    name: 'PowerPLant' + id,
                    type: type,
                    previousDecisions: [SATURATED, OVER_SATURATED, SATURATED, OVER_SATURATED, UNDER_SATURATED],
                    connections:[{[this.state.source.id]:0}]
                }
                this.setState(prevState => ({
                    plants:plants,
                    links: [
                        ...prevState.data.links,
                        {
                            source: prevState.idCounter,
                            target: prevState.source.id
                        }
                    ],
                    idCounter: prevState.idCounter + 1,

                }))



            }

        }
       this.formatData()
       console.log(this.state.links)
    }
    handleAddEdge() {
        const source = this.state.source
        const drain = this.state.drain
        console.log(source)
        // this.setState(prevState=>{
        //     //plants[source.id].connections[drain.id]:0

        //     }
        // })
    }
